// import { store } from "./store";

let Security = {
  saveToken: (token) => {
    let date = new Date();
    let expDays = 30;
    date.setTime(date.getTime() + expDays * 24 * 60 * 60 * 1000);
    const expires = "expires=" + date.toUTCString();
    document.cookie =
      "token=" +
      JSON.stringify(token) +
      ";" +
      expires +
      ";path=/" +
      ";" +
      "SameSite=Strict;" +
      "Secure";
  },
  setTokenNull: () => {
    document.cookie =
      "token=" +
      ";" +
      "expires=1970-01-01T00:00:00.000" +
      ";path=/" +
      ";" +
      "SameSite=Strict;" +
      "Secure";
  },
};

export default Security;
