export default {
  name: "FromTag",
  props: ["method", "action", "name", "event"],
  methods: {
    submit() {
      let myForm = this.$refs[this.$props.name];
      if (myForm.checkValidity()) {
        this.$emit(this.$props['event']);
      }
      myForm.classList.add("was-validated");
    }
  }
};
