import Security from "./security";
export default {
  name: "ServiceEdit",
  data() {
    return {
      ready: false,
      service: {
        id: 0,
        name: "",
        description: "",
        m_amount: "",
        cost: 0
      }
    };
  },
  beforeMount() {
    Security.isAdmin();
    if (this.$route.params.serviceId != -0) {
      fetch(`${process.env.VUE_APP_SERVER_API}/services/${this.$route.params.serviceId}`, Security.requestOptionsWithoutBody("GET")).then(response => response.json()).then(response => {
        if (response.error) {
          this.$emit("error", response.message);
        } else {
          this.service = response.data;
          this.ready = true;
        }
      }).catch(err => this.$emit("error", err));
    } else {
      this.ready = true;
    }
  }
};
